import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Dropdown, Grid, Button, Header} from 'semantic-ui-react';

let actSetList = [];
function getOptions() {
  actSetList.length = 0;
  for (const i in window.localStorage) {
    if (window.localStorage.getItem(i) && i !== "ally-supports-cache") {
      actSetList.push(JSON.parse(window.localStorage.getItem(i)));
    }
  }
}

export default class MainMenu extends Component {
  componentDidMount() {
    window.addEventListener('load', getOptions);
  }

  componentWillUnmount() {
    window.removeEventListener('load', getOptions)
  }

  render() {
    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign:'right'}}>Choose Activity Set</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown
              placeholder='Activity Set'
              search
              selection
              options={actSetList}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column width={10}>
              <Header size='huge'>My break will now be...</Header>
            </Grid.Column>
            <Grid.Column width={6}>
              <Button size='massive' primary floated='right' content='Break Time!' />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
