import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Grid, Dropdown, Input, Button} from 'semantic-ui-react';

let actSetList = [
  {
    key: 'Activity Set 1',
    text: 'Activity Set 1',
    value: 'Activity Set 1'
  },
  {
    key: 'Activity Set 2',
    text: 'Activity Set 2',
    value: 'Activity Set 2'
  }
];

export default class CreateList extends Component {
  state={actList: ''};

  handleAddition = (e, {value}) => {
    actSetList.push({key: value, text: value, value});
  };
  handleChange = (e, {value}) => this.setState({ actList: value });

  render() {
    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign: 'right'}}>Saved Activity Sets</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown
                fluid
                placeholder='Activity Set'
                search
                selection
                options={actSetList}
                allowAdditions
                additionLabel='New Activity Set: '
                onAddItem={this.handleAddition}
                onChange={this.handleChange}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column width={6}>
            </Grid.Column>
            <Grid.Column width={4}>
              <Input
                size='large'
                placeholder='Title'
                defaultValue={this.state.actList}
              />
            </Grid.Column>
            <Grid.Column width={2}>
              <Button content='Edit Title' primary/>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
