import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Grid, Dropdown, Input, Button, Message} from 'semantic-ui-react';

let actSetList = [
  {
    key: 'Activity Set 1',
    text: 'Activity Set 1',
    value: 'Activity Set 1',
    activities: []
  },
  {
    key: 'Activity Set 2',
    text: 'Activity Set 2',
    value: 'Activity Set 2',
    activities: []
  }
];

export default class CreateList extends Component {
  state={
    actList: '',
    title: ''
  };

// Adds activity list into data
  handleAddition = (e, {value}) => actSetList.push({key: value, text: value, value});

// Changes title display to match current value
  handleChange = (e, {value}) => this.setState({actList: value});

// Keeps track of title for future use
  handleInputChange = (e, {value}) => this.setState({actList: value, title: value});

// Edits the title of the active activity list
  handleEdit = (e, {value}) => {
    const index = actSetList.map(function(e) {
      return e.key;
    }).indexOf(this.state.title);
    actSetList[index].key = value;
    actSetList[index].text = value;
    actSetList[index].value = value;
  };

  render() {
    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign: 'right'}}>Saved Activity Sets</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown
                placeholder='Activity Set'
                search
                selection
                options={actSetList}
                allowAdditions
                additionLabel='New Activity Set: '
                onAddItem={this.handleAddition}
                onChange={this.handleChange}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column textAlign='center'>
              <Input
                action={{
                  color: 'blue',
                  content: 'Edit Title',
                  onClick: () => this.handleEdit()
                }}
                size='large'
                placeholder='Title'
                value={this.state.actList}
                onChange={this.handleInputChange}
              />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
