import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Grid, Dropdown} from 'semantic-ui-react';

let actSetList = [
  {
    key: 'Activity Set 1',
    text: 'Activity Set 1',
    value: 'Activity Set 1'
  },
  {
    key: 'Activity Set 2',
    text: 'Activity Set 2',
    value: 'Activity Set 2'
  }
];

export default class CreateList extends Component {
  state = {actSetList};

  handleAddition = (e, {value}) => {
    this.setState({actSetList: [{text: value, value}]});
  };

  render() {
    const {currentValue} = this.state;

    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign:'right'}}>Saved Activity Sets</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown
                fluid
                placeholder='Activity Set'
                search
                selection
                options={actSetList}
                allowAdditions
                additionLabel='New Activity Set: '
                onAddItem={this.handleAddition}
                onChange={this.handleAddition}
              />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
