import React, {Component} from 'react';
import {Menu, Message, Button} from 'semantic-ui-react';
import {Link} from '../routes';

export default class Header extends Component {
  state = {activeItem: ''};

  setMain = event => {
    this.setState({activeItem: 'Main'});
  };

  render() {
    const {activeItem} = this.state;

    return (
      <Menu header
        size='massive'
        style={{marginTop: '10px'}}
      >
        <Menu.Item header
          name='BREAK RANDOMIZER'
          position='right'
        />
        <Menu.Menu
          position='right'
        >
          <Link route='/main'>
            <a>
              <Button
                content='Main'
                primary
                style={{marginBottom: '0'}}
              />
            </a>
          </Link>
          <Link route='/edit'>
          <a>
            <Button
              content='Edit'
              secondary
            />
          </a>
          </Link>
        </Menu.Menu>
      </Menu>
    );
  }
}
