import React, {Component} from 'react';
import {Menu, Message} from 'semantic-ui-react';
import {Link} from '../routes';

export default class Header extends Component {
  state = {activeItem: ''};

  // handleItemClick = (e, {item}) => this.setState({ activeItem: item });
  handleItemClick = ({name}) => {
    console.log(this);
  };

  render() {
    const {activeItem} = this.state;

    return (
      <Menu tabular
        size='massive'
        style={{marginTop: '10px'}}
      >
        <Menu.Item header
          name='BREAK RANDOMIZER'
          position='right'
        />
        <Menu.Menu
          position='right'
        >
          <Link route='/main'>
            <a
              name='Main'
              className='item'
              active={activeItem ? 'Main' : undefined}
              onClick={this.handleItemClick}
            >
              Main
            </a>
          </Link>
          <Link route='/edit'>
            <a
              className='item'
              active={activeItem ? 'Edit' : undefined}
              onClick={this.handleItemClick}
            >
              Edit
            </a>
          </Link>
        </Menu.Menu>
      </Menu>
    );
  }
}
