import React, {Component} from 'react';
import {Menu, Message, Button} from 'semantic-ui-react';
import {Link} from '../routes';

export default class Header extends Component {
  state = {activeItem: ''};

  setMain = event => {
    this.setState({activeItem: 'Main'});
  };

  render() {
    const {activeItem} = this.state;

    return (
      <Menu header
        size='massive'
        style={{marginTop: '10px'}}
      >
        <Menu.Item header
          name='BREAK RANDOMIZER'
        />
        <Menu.Menu widths={2}>
          <Link route='/main'>
            <a className='item'>
              Main
            </a>
          </Link>
          <Link route="/campaigns/new">
            <a className="item">
              +
            </a>
          </Link>
        </Menu.Menu>
      </Menu>
    );
  }
}
