import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Dropdown, Grid, Button, Header} from 'semantic-ui-react';

let actSetList = [];

export default class MainMenu extends Component {
  render() {
    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign:'right'}}>Choose Activity Set</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown fluid placeholder='Activity Set' search selection options={actSetList}/>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column width={10}>
              <Header size='huge'>My break will now be...</Header>
            </Grid.Column>
            <Grid.Column width={6}>
              <Button size='massive' primary floated='right' content='Break Time!' />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
