import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Grid, Dropdown, Input, Button, Message} from 'semantic-ui-react';

let actSetList = [
  {
    key: 'Activity Set 1',
    text: 'Activity Set 1',
    value: 'Activity Set 1',
    activities: [
      {
        name: 'Reading',
        weight: 2
      },
      {
        name: 'Writing',
        weight: 5
      }
    ]
  },
  {
    key: 'Activity Set 2',
    text: 'Activity Set 2',
    value: 'Activity Set 2',
    activities: [
      {
        name: 'Running',
        weight: 3
      },
      {
        name: 'Biking',
        weight: 1
      }
    ]
  }
];

function findIndex() {
  const index = actSetList.map(function(e) {
    return e.key;
  }).indexOf(this.state.title);
  return {index};
}

export default class CreateList extends Component {
  state={
    actList: '',
    title: ''
  };

// Adds activity list into data
  handleAddition = (e, {value}) => actSetList.push({key: value, text: value, value});

// Changes title display to match current value and keeps track of true title
  handleChange = (e, {value}) => this.setState({actList: value, title: value});

// Allows changes in input field
  handleInputChange = (e, {value}) => this.setState({actList: value});

// Deletes the activity list
  handleDelete = () => {
    findIndex();
    actSetList.splice(index, 1);
    this.setState({actList: '', title: ''})
  };

// Edits the title of the active activity list
  handleEdit = () => {
    const index = actSetList.map(function(e) {
      return e.key;
    }).indexOf(this.state.title);
    if (this.state.title) {
      actSetList[index].key = this.state.actList;
      actSetList[index].text = this.state.actList;
      actSetList[index].value = this.state.actList;
      this.setState({title: this.state.actList});
    }
  };

// Adds an activity to the activity list
  handleAdd = () => {

  };

// Removes an activity from the activity list
  handleRemove = () => {

  };

// Modifies the name of an activity
  handleModify = () => {

  };

// Increments the weight of an activity
  handleIncrement = () => {

  }

// Decrements the weight of an activity
  handleDecrement = () => {

  };

  renderActivities() {
    return(
      <Grid.Row>
        <Grid.Column
          width={2}
          textAlign='right'
          verticalAlign='middle'
        >
          <Button
            circular
            negative
            icon='minus'
            size='medium'
            onClick={this.handleRemove}
          />
        </Grid.Column>
        <Grid.Column width={9}>
          <Input
            action={{
              color: 'blue',
              content: 'Edit Name',
              onClick: this.handleModify
            }}
            size='small'
            fluid
          />
        </Grid.Column>
        <Grid.Column width={1}>
          <Button
            circular
            negative
            icon='minus'
            size='mini'
            onClick={this.handleDecrement}
          />
        </Grid.Column>
        <Grid.Column width={3}>
        </Grid.Column>
        <Grid.Column width={1}>
          <Button
            circular
            positive
            icon='plus'
            size='mini'
            onClick={this.handleIncrement}
          />
        </Grid.Column>
      </Grid.Row>
    );
  }

  render() {
    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign: 'right'}}>Saved Activity Sets</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown
                placeholder='Activity Set'
                search
                selection
                options={actSetList}
                allowAdditions
                additionLabel='New Activity Set: '
                onAddItem={this.handleAddition}
                onChange={this.handleChange}
                value={this.state.title}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column textAlign='center'>
              <Button
                negative
                icon='minus'
                size='large'
                onClick={this.handleDelete}
              />
              <Input
                action={{
                  color: 'blue',
                  content: 'Edit Title',
                  onClick: () => this.handleEdit()
                }}
                size='large'
                placeholder='Title'
                value={this.state.actList}
                onChange={this.handleInputChange}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column width={2}></Grid.Column>
            <Grid.Column
              width={9}
              textAlign='center'
              verticalAlign='bottom'
            >
              <Message
                color='blue'
                header='Activity Name'
                content='Name of your break activity.'
              />
            </Grid.Column>
            <Grid.Column
              width={5}
              textAlign='center'
            >
              <Message
                color='red'
                header='Weight'
                content='How much you want to weight an activity to show up. Each unit is like adding another entry into a lottery.'
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column
              width={2}
              textAlign='right'
              verticalAlign='middle'
            >
              <Button
                circular
                positive
                icon='plus'
                size='medium'
                onClick={this.handleAdd}
              />
            </Grid.Column>
            <Grid.Column width={9}>
              <Input
                size='small'
                placeholder='Name'
                fluid
              />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
