import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Grid, Dropdown, Input, Button, Message, Popup} from 'semantic-ui-react';

let actSetList = [];

export default class CreateList extends Component {
  state={
    actList: '',
    title: '',
    tempName: ''
  };

  test = () => {
    console.log('this works');
  }

// Translates data from local storage into an array
  getOptions = () => {
    for (const i in window.localStorage) {
      if (window.localStorage.getItem(i)) {
        console.log(window.localStorage.getItem(i));
      }
    }
  }

// Adds activity list into local storage
  handleAddition = (e, {value}) => {
    window.localStorage.setItem(value, JSON.stringify({key: value, text: value, value, activities: []}));
    console.log(actSetList);
  }

// Changes title display to match current value and keeps track of true title
  handleChange = (e, {value}) => this.setState({actList: value, title: value});

// Allows changes in input field
  handleInputChange = (e, {value}) => this.setState({actList: value});

// Deletes the activity list
  handleDelete = () => {
    window.localStorage.removeItem(this.state.title);
    this.setState({actList: '', title: ''})
  };

// Ensures no accidental deletion
  handleAntidelete = () => {
    if (this.state.actList) {
      return(
        <Button
          negative
          content='Comfirm Delete'
          onClick={this.handleDelete}
        />
      );
    } else {
      return(
        <Message
          negative
          content='You cannot delete nothing!'
        />
      );
    }
  }

// Edits the title of the active activity list
  handleEdit = () => {
    if (this.state.title) {
      const activities = JSON.parse(window.localStorage.getItem(this.state.title)).activities;
      window.localStorage.removeItem(this.state.title);
      window.localStorage.setItem(value, JSON.stringify({key: this.state.actList, text: this.state.actList, value: this.state.actList, activities}));
      this.setState({title: this.state.actList});
    }
  };

// Adds an activity to the activity list
  handleAdd = () => {

  };

// Removes an activity from the activity list
  handleRemove = () => {

  };

// Modifies the name of an activity
  handleModify = () => {

  };

// Increments the weight of an activity
  handleIncrement = () => {

  }

// Decrements the weight of an activity
  handleDecrement = () => {

  };

  renderActivities() {
    if (this.state.title) {
      return (
        JSON.parse(window.localStorage.getItem(this.state.title)).activities.map(activity => {
          return(
            <Grid.Row key={activity.key}>
              <Grid.Column
                width={2}
                textAlign='right'
                verticalAlign='middle'
              >
                <Button
                  circular
                  negative
                  icon='minus'
                  size='medium'
                  onClick={this.handleRemove}
                />
              </Grid.Column>
              <Grid.Column width={9}>
                <Input
                  action={{
                    color: 'blue',
                    content: 'Edit Name',
                    onClick: this.handleModify
                  }}
                  size='small'
                  fluid
                  defaultValue={activity.key}
                />
              </Grid.Column>
              <Grid.Column
                width={2}
                textAlign='right'
                verticalAlign='middle'
              >
                <Button
                  circular
                  negative
                  icon='minus'
                  size='mini'
                  onClick={this.handleDecrement}
                />
              </Grid.Column>
              <Grid.Column
                width={1}
                textAlign='center'
                verticalAlign='middle'
              >
                <h2>{activity.weight}</h2>
              </Grid.Column>
              <Grid.Column
                width={2}
                verticalAlign='middle'
              >
                <Button
                  circular
                  positive
                  icon='plus'
                  size='mini'
                  onClick={this.handleIncrement}
                />
              </Grid.Column>
            </Grid.Row>
          );
        })
      );
    }
  }

  render() {
    this.getOptions;
    return(
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column width={8}>
              <h2 style={{textAlign: 'right'}}>Saved Activity Sets</h2>
            </Grid.Column>
            <Grid.Column width={5}>
              <Dropdown
                placeholder='Activity Set'
                search
                selection
                options={actSetList}
                allowAdditions
                additionLabel='New Activity Set: '
                onAddItem={this.handleAddition}
                onChange={this.handleChange}
                value={this.state.title}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column textAlign='center'>
              <Popup
                trigger={
                  <Input
                    size='large'
                    placeholder='Title'
                    value={this.state.actList}
                    onChange={this.handleInputChange}
                    action
                  >
                    <Popup
                      trigger={
                        <Button
                          negative
                          icon='minus'
                          size='large'
                        />
                      }
                      content={this.handleAntidelete}
                      on='click'
                      position='top center'
                    />
                    <input />
                    <Button
                      color='blue'
                      content='Edit Title'
                      onClick={this.handleEdit}
                      disabled={!this.state.actList}
                    />
                  </Input>
                }
                content={
                  <Message
                    negative
                    content='You cannot name an activity set nothing!'
                  />
                }
                position='top center'
                disabled={!!this.state.actList}
              />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column width={2}></Grid.Column>
            <Grid.Column
              width={9}
              textAlign='center'
              verticalAlign='bottom'
            >
              <Message
                color='blue'
                header='Activity Name'
                content='Name of your break activity.'
              />
            </Grid.Column>
            <Grid.Column
              width={5}
              textAlign='center'
            >
              <Message
                color='red'
                header='Weight'
                content='How much you want to weight an activity to show up. Each unit is like adding another entry into a lottery.'
              />
            </Grid.Column>
          </Grid.Row>
          {this.renderActivities()}
          <Grid.Row>
            <Grid.Column
              width={2}
              textAlign='right'
              verticalAlign='middle'
            >
              <Button
                circular
                positive
                icon='plus'
                size='medium'
                onClick={this.handleAdd}
              />
            </Grid.Column>
            <Grid.Column width={9}>
              <Input
                size='small'
                placeholder='Name'
                fluid
              />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}
